function TinyMCE_cutnews_getInfo(){return{longname:'Cutnews',author:'Torbstoff',authorurl:'',infourl:'',version:tinyMCE.majorVersion+"."+tinyMCE.minorVersion};};function TinyMCE_cutnews_getControlHTML(control_name){switch(control_name){case "cutnews":var cmd='insertatcursor(\'[CUT]\');return false;';return '<a href="javascript:'+cmd+'" onclick="'+cmd+'" target="_self" onmousedown="return false;"><img id="{$editor_id}_cutnews" src="{$pluginurl}/images/cutnews.gif" title="Funktion: Weiterlesen" width="20" height="20" class="mceButtonNormal" onmouseover="tinyMCE.switchClass(this,\'mceButtonOver\');" onmouseout="tinyMCE.restoreClass(this);" onmousedown="tinyMCE.restoreAndSwitchClass(this,\'mceButtonDown\');" /></a>';}return "";}function TinyMCE_cutnewst_execCommand(editor_id,element,command,user_interface,value){switch(command){case "mceCutnews":tinyMCE.getInstanceById(editor_id).contentWindow.cutnews();return true;}return false;}